import React, { useState } from 'react';
import { Input } from '@alifd/next';
import './App.css';
import Lunch from './pages/Lunch';

const App = () => {
  const [tFilterStr, setTFilterStr] = useState('');
  const [payAllTotal, setPayAllTotal] = useState(0);
  const [payOkTotal, setPayOkTotal] = useState(0);


  function searchRender(value) {//input search filter

    setTFilterStr(value);
  }
  return (
    <div className="App">
      <h1>EZCON點餐系統後台</h1>
      <div className="box">
        <Input

          size="large"
          placeholder="輸入搜尋文字"
          onChange={searchRender}
          aria-label="this is input"
        />
        <h1 className="payText">總金額:{payAllTotal}元</h1>
        <h1  className="payText">已收:{payOkTotal}元</h1>
      </div>
      <br /><br />
      <Lunch filterStr={tFilterStr} setPayAllTotal={setPayAllTotal} setPayOkTotal={setPayOkTotal}/>

    </div>
  );
}

export default App;
