import React, { useState, useEffect } from 'react';
import OrderTable from '../../components/OrderTable';

const  Order =(props)=> {
    const tarProps = {
        dbName: "orders",
        keys: {
            name: {
                type: "input",
                text: "姓名"
            },
            text: {
                type: "input",
                text: "文字"
            },
            money: {
                type: "input",
                text: "金額"
            },


        }
    }
    const [myListData, setMyListData] = useState([]);
    const [myListDataAll, setMyListDataAll] = useState([]);



    useEffect(() => {

        const sortedArr = [...myListDataAll].map((e) => {
            let hasIncludes = false;
            Object.keys(e).forEach((ee) => {
                let val2 = e[ee];
                if (val2.toString().includes(props.filterStr)) hasIncludes = true;
            })
            if (hasIncludes) return e;
            return false;
        }).filter((e) => e !== false);
        setMyListData([...sortedArr]);
    }, [props.filterStr]);
    return (
        <>

                <OrderTable tarProps={tarProps} listData={myListData} listDataProps={data => setMyListData([...data])} listDataAllProps={data => setMyListDataAll([...data])} />

        </>
    );
}

export default Order;
