import React,{ useState, useEffect ,Fragment}  from 'react';
import { Button, Dialog ,Input,Form,Select,DatePicker,Table} from '@alifd/next';
import moment from 'moment';
const Option = Select.Option;
const { Item } = Form;


const OrderTable = (props) => {
  const { dbName, keys } = props.tarProps;
  const [listData, setListData] = useState([]);
  const [writeData, setWriteData] = useState({});
  const [popupState, setPopupState] = useState(false);
  const [editKey, setEditKey] = useState(false);
  const [editIndex, setEditIndex] = useState("");

  // console.log("writeData", writeData);

  const addCreateData = (key, value) => {
    //-------Input OnChange Event
    let obj = {};
    obj[key] = value;
    // console.log("===obj", obj);
    setWriteData({ ...writeData, ...obj });
  };
  const createSend = () => {
    //-------Create Data 送出
    if (editKey == true) {
      //Check is edit state
      listData[editIndex] = writeData;

      setListData([...listData]);
      setWriteData({});
    } else {
      setListData([...listData, { ...writeData }]);
      setWriteData({});
    }
  };

  useEffect(() => {
    if (props.tarData) {
      setListData([...props.tarData]);
      //console.log('??',props.tarData);
    }
  }, []);

  const typeComponents = (e, type) => {
    // Render Type Components
    let element;
    switch (type) {
      case "input":
        element = (
          <Input
            value={writeData[e]}
            onChange={(value) => {
              addCreateData(e, value);
            }}
            placeholder={e}
          />
        );
        break;
      case "datePicker":
        element = (
          <DatePicker
            value={writeData[e]}
            placeholder={e}
            onChange={(value) => {
              addCreateData(e, moment(value).format("YYYY-MM-DD"));
            }}
          />
        );
        break;
      case "textArea":
        element = (
          <Input.TextArea
            value={writeData[e]}
            placeholder={e}
            maxLength={500}
            rows={6}
            hasLimitHint
            aria-label="input max length 500"
            onChange={(value) => {
              addCreateData(e, value);
            }}
          />
        );
        break;
    }
    return element;
  };

  const deleteEvent = (id) => {
    // Delete Data
    let updatedData = [...listData].filter((x) => x.id !== id);
    setListData(updatedData);
  };

  return (
    <>
      <Dialog
        hasMask={false}
        visible={popupState}
        onOk={() => {
          createSend();
          setPopupState(false);
        }}
        onCancel={() => {
          setPopupState(false);
          setWriteData({});
        }}
        onClose={() => {
          setPopupState(false);
          setWriteData({});
        }}
      >
        {[...Object.keys(keys)].map((e, nb) => {
          //-------Render Create Element
          const type = keys[e].type;

          return (
           <Fragment key={keys[e].text}>
              <Form> 
                <Item  label={keys[e].text}>{typeComponents(e, type)}</Item>
              </Form>
            </Fragment>
          );
        })}
      </Dialog>

      <Button
        type="primary"
        onClick={() => {
          setPopupState(true);
        }}
      >
        新增
      </Button>

      <Table dataSource={listData}>
        {[...Object.keys(keys)].map((list) => {
          //  -------Render Table Data

          return <Table.Column key={keys[list].text} title={keys[list].text} dataIndex={list} />;
        })}
        <Table.Column
          title="操作"
          cell={(value, index, record) => {
            return (
              <>
                <Button
                  type="normal"
                  onClick={() => {
                    setPopupState(true);
                    setEditKey(true);
                    setEditIndex(index);
                    setWriteData({ ...listData[index] });
                  }}
                >
                  編輯
                </Button>

                <Button
                  type="primary"
                  warning
                  onClick={() => {
                    deleteEvent(record.id);
                  }}
                >
                  刪除
                </Button>
              </>
            );
          }}
        />
      </Table>
    </>
  );
};

export default OrderTable;
